package com.example.listeplanetes;import android.graphics.Color;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.*;import androidx.appcompat.app.AppCompatActivity;import android.os.Bundle;import java.util.ArrayList;public class MainActivity extends AppCompatActivity {    protected ArrayList<String> planetes;    ListView listview;    PlaneteAdapter adapter;    Button button;    Data d;    @Override    protected void onCreate(Bundle savedInstanceState) {        d = new Data(this);        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        button = (Button)findViewById(R.id.verif);        d.installePlanetes();        listview = (ListView) findViewById(R.id.listView);        adapter = new PlaneteAdapter(this);        listview.setAdapter(adapter);        button.setOnClickListener(btnClick);    }    private final View.OnClickListener btnClick = new View.OnClickListener() {        @Override        public void onClick(View v) {            button.setBackgroundColor(Color.GRAY);            int score = 0;            String [] taille = d.taillePlanetes;            for(int i=0; i< d.taillePlanetes.length; i++){                View vw = listview.getChildAt(i);                Spinner sp = vw.findViewById(R.id.spinner);                if(sp.getSelectedItem().toString().equals(d.taillePlanetes[i])){                    score+=1;                }            }            Toast.makeText(MainActivity.this, "Score = "+ score+ "/" + d.taillePlanetes.length, Toast.LENGTH_SHORT).show();        }    };}