package com.example.listeplanetes;import android.content.Context;import android.graphics.Color;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.*;import com.example.listeplanetes.MainActivity;import com.example.listeplanetes.R;public class PlaneteAdapter extends BaseAdapter {    public static int compteur = 0;    MainActivity m;    Data d;    public PlaneteAdapter(MainActivity mainActivity) {        this.m = mainActivity;        d = new Data(m);    }    @Override    public int getCount() {        return m.planetes.size();    }    @Override    public Object getItem(int position) {        return m.planetes.get(position);    }    @Override    public long getItemId(int position) {        return 0;    }    @Override    public int getViewTypeCount() {        return getCount();    }    @Override    public int getItemViewType(int position) {        return position;    }    @Override    public View getView(int position, View convertView, ViewGroup parent) {        View itemView = convertView;        if (convertView == null) {            LayoutInflater inflater = (LayoutInflater)m.getSystemService(Context.LAYOUT_INFLATER_SERVICE);            itemView = inflater.inflate(R.layout.listitem, null);        }        TextView nomPlanete = (TextView) itemView.findViewById(R.id.textView);        final CheckBox checkBox = (CheckBox) itemView.findViewById(R.id.checkbox);        final Spinner spinner = (Spinner) itemView.findViewById(R.id.spinner);        final Button button = (Button) itemView.findViewById(R.id.button);        final ArrayAdapter<String> spinadapter = new ArrayAdapter<String>(m, android.R.layout.simple_spinner_item, d.taillePlanetes);        spinadapter.setDropDownViewResource(android.R.layout.simple_spinner_dropdown_item);        spinner.setAdapter(spinadapter);        checkBox.setOnCheckedChangeListener(new CompoundButton.OnCheckedChangeListener() {            @Override            public void onCheckedChanged(CompoundButton compoundButton, boolean b) {                CheckBox checkBox = (CheckBox)  compoundButton.findViewById(R.id.checkbox);                spinner.setEnabled(!checkBox.isChecked());                spinadapter.notifyDataSetChanged();                if(checkBox.isChecked()) {                    compteur++;                    Toast.makeText(m, "checked " + compteur, Toast.LENGTH_LONG).show();                }else{                    compteur--;                    m.button.setEnabled(false);                    m.button.setBackgroundColor(Color.argb(100,142,168,182));                }                if(compteur == getCount()){                    m.button.setEnabled(true);                }            }        });        nomPlanete.setText(m.planetes.get(position));        return itemView;    }}